class Sound {
  constructor( data ) {
    this.volumeTics = new Int8Array( 32 );
    this.pitchTics = new Int8Array( 32 );
    this.arpTics = new Int8Array( 32 );
    this.useLoop = false;
    this.loopStart = -1;
    this.loopEnd = -1;
    this.wave = 0;
    this.pitchScale = 10;
    this.releaseLength = 1;
    this.releaseMode = Sound.RELEASE_LINEAR;

    this.isPlayingInfiniteSound = false;
    this.infiniteStartTime = 0;
    this.infiniteOsc = null;
    this.infiniteGain = null;
    this.infiniteTicDuration = 0;
    this.infiniteTicsPlayed = 0;
    this.infiniteVolume = 0;
    this.infiniteNote = 0;

    if ( data ) {
      const {
        volumeTics,
        pitchTics,
        arpTics,
        useLoop,
        loopStart,
        loopEnd,
        wave,
        pitchScale,
        releaseLength,
      } = data;

      if ( volumeTics && Array.isArray( volumeTics ) ) {
        for ( let i = 0; i < volumeTics.length; i += 1 ) {
          this.volumeTics[i] = volumeTics[i];
        }
      }

      if ( pitchTics && Array.isArray( pitchTics ) ) {
        for ( let i = 0; i < pitchTics.length; i += 1 ) {
          this.pitchTics[i] = pitchTics[i];
        }
      }

      if ( arpTics && Array.isArray( arpTics ) ) {
        for ( let i = 0; i < arpTics.length; i += 1 ) {
          this.arpTics[i] = arpTics[i];
        }
      }

      this.wave = wave;
      this.useLoop = useLoop;
      this.loopStart = loopStart;
      this.loopEnd = loopEnd;
      this.pitchScale = pitchScale;
      this.releaseLength = releaseLength;
    }
  }
}

Sound.RELEASE_LINEAR = 'linear';
Sound.RELEASE_EXPO = 'expo';

export default Sound;

import Screen from '../Screen/Screen';
import Input from '../Input/Input';
import TileData from '../TileData/TileData';
import MapData from '../MapData/MapData';
import FontData from '../FontData/FontData';
import Audio from '../Audio/Audio';

import standardFont from '../FontData/standard.font.json';
import smallFont from '../FontData/small.font.json';

class Engine {
  constructor() {
    this.containerId = 'bitmelo-container';
    this.onInit = null;
    this.onDrawStartScreen = null;
    this.onUpdateStartTransition = null;
    this.startTransitionFrames = 60;
    this.onUpdate = null;
    this.clickToBegin = true;
    this.screen = new Screen();
    this.input = new Input();
    this.tileData = new TileData();
    this.mapData = new MapData();
    this.fontData = new FontData();
    this.audio = new Audio();

    this.fontData.addFont( standardFont );
    this.fontData.addFont( smallFont );

    this._hasBegun = false;
    this._update = this._update.bind( this );
  }

  /**
   * Begin running the engine. This will perform initial setup, call the onInit function, and begin the game loop
   */
  begin() {
    if ( this.onInit ) {
      this.onInit();
    }

    this.screen.conainerId = this.containerId;
    this.screen.init();
    this.screen.onScaleChange = ( scale ) => {
      this.input.canvasScale = scale;
    };

    this.input.canvas = this.screen.canvas;
    this.input.canvasScale = this.screen.scale;
    this.input.screenWidth = this.screen.width;
    this.input.screenHeight = this.screen.height;
    this.input.init();
    this.tileData.init();
    this.screen.tileData = this.tileData;
    this.screen.mapData = this.mapData;
    this.screen.fontData = this.fontData;

    if ( this.clickToBegin ) {
      if ( this.onDrawStartScreen ) {
        this.onDrawStartScreen();
      }
      else {
        this.screen.clear( 1 );
        this.screen.drawText( 'Click to begin...', 10, 10, 2, 1, 0 );
      }
      const screenHidesCursor = this.screen.hideCursor;
      this.screen.hideCursor = false;
      this.screen._setCanvasStyle();
      this.screen.drawScreen();
      this.screen.canvas.addEventListener( 'click', () => {
        if ( !this._hasBegun ) {
          this._hasBegun = true;
          this.screen.hideCursor = screenHidesCursor;
          this.screen._setCanvasStyle();
          this.audio.init();
          this.input.clearInput();
          requestAnimationFrame( this._update );
        }
      } );
    }
    else {
      this._hasBegun = true;
      this.audio.init();
      requestAnimationFrame( this._update );
    }
  }

  /**
   * Game loop
   */
  _update() {
    this.input.pollInput();

    if ( this.startTransitionFrames > 0 ) {
      this.startTransitionFrames -= 1;
      if ( this.onUpdateStartTransition ) {
        this.clearInput();
        this.onUpdateStartTransition();
      }
      else {
        this.screen.clear( 1 );
      }
    }
    else if ( this.onUpdate ) {
      this.onUpdate();
      this.audio.update();
    }

    this.screen.drawScreen();
    requestAnimationFrame( this._update );
  }
}

export default Engine;

/**
 * Represents a font. Data is stored similarly to tilesets in an array, with the ability to remap characters
 * that have a unicode point larger than the array can hold.
 */
class Font {
  constructor( fontData ) {
    this.standardWidth = 5;
    this.letterSpacing = 1;

    this.tileSize = 16;
    this.width = 16;
    this.height = 16;
    this.originX = 1;
    this.originY = 3;

    this.charData = null;
    this.data = null;

    if ( fontData ) {
      this.standardWidth = fontData.standardWidth;
      this.letterSpacing = fontData.letterSpacing;

      this.tileSize = fontData.tileSize;
      this.width = fontData.width;
      this.height = fontData.height;
      this.originX = fontData.originX;
      this.originY = fontData.originY;
      this.charData = fontData.charData;

      this.data = new Uint8ClampedArray( this.width * this.height * this.tileSize * this.tileSize );
      const { data } = fontData;
      let runPosition = 0;
      let dataPosition = 0;
      while ( runPosition < data.length ) {
        const runLength = data[runPosition];
        const paletteId = parseInt( data[runPosition + 1], 10 );
        for ( let j = 0; j < runLength; j += 1 ) {
          this.data[dataPosition] = paletteId;
          dataPosition += 1;
        }
        runPosition += 2;
      }
    }
  }

  /**
   * Get the base index in the data array for the character
   * @param {number} charCode - the unicode point for the character
   */
  baseIndexForChar( charCode ) {
    let codePoint = charCode;
    if ( charCode >= this.width * this.height ) {
      const key = charCode.toString();

      if ( this.charData && this.charData[key] ) {
        if ( this.charData[key] !== undefined ) {
          codePoint = this.charData[key].remap;
        }
      }
    }
    return codePoint * this.tileSize * this.tileSize;
  }

  /**
   * Get the width of a character
   * @param {number} charCode - the unicode point for the character
   */
  widthForChar( charCode ) {
    const charKey = charCode.toString();
    if ( this.charData && this.charData[charKey] ) {
      if ( this.charData[charKey].width !== undefined ) {
        return this.charData[charKey].width;
      }
    }
    return this.standardWidth;
  }
}

export default Font;

import Font from './Font';

/**
 * Holds all of the font data
 */
class FontData {
  constructor() {
